div{
    width: 70vw;
    background-color: grey;
    color: white;
    padding: 12px;f
    border-radius: 12px;
    margin: auto;
    text-align: center;
}
ul{
    list-style-type: none;
    padding: 0%;
    margin:  24px auto;
}
li{
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 12px ;
    border:  2px solid black;
    background-color:  #00557f;
    border-radius:  5px;
    margin:  5px;
}
 h1{
    font-size:  36px;
 }
 h2{
    margin-right: 24px;
 }
  input{
    font-size: 24px;
    padding:  8px 8px;
    outline:  none ;
    width:  50%;

  }
  .delete-button {
    background: none;
    color: white;
    border: 1px solid transparent;
    border-radius: 100%;
    height: 24px;
    width: 24px;
    font-size: 14px;
    font-weight: bold;
    padding: 0;
    transition: 0.5s;
    cursor: pointer;
}
#add-button {
    font-size: 24px;
    padding: 8px 12px;
    border-radius: 8px;
    background-color: lightblue;
    transition: 0.5s;
    cursor: pointer;
  }

.delete-button:hover {
    border-color: white;
    transform: scale(1.2);
}
#add-button:disabled{
    background-color: revert;
    cursor: default;
}
.wrapper{
    width: 70vw;
    margin: auto;
    text-align: center;
    background-color: grey;
    color: white;
}
